import React, { useEffect } from "react";
import { withFormik, Form, Field } from "formik";
import { Container, Segment, Header } from "semantic-ui-react";
import * as Yup from "yup";

const SignUp = ({ errors, touched, status, updateUser, values }) => {
    useEffect(() => {
        if (status) {
            updateUser(status)
        }
    }, [status])
  return (
    <Container className="Login-Page">
      <Header>Welcome Professor!</Header>
      <Header>Fill Out the Sections Below to Sign Up</Header>
      <Form>
        <Header>All Fields Are Required</Header>
        <Segment>First Name:</Segment>
        <Field type="text" name="firstName" />
        {touched.firstName && errors.firstName && (
          <Segment className="error">{errors.firstName}</Segment>
        )}
        <Segment>Last Name:</Segment>
        <Field type="text" name="lastName" />
        {touched.lastName && errors.lastName && (
          <Segment className="error">{errors.lastName}</Segment>
        )}
        <Segment>Email Address:</Segment>
        <Field type="email" name="email" />
        {touched.email && errors.email && (
          <Segment className="error">{errors.email}</Segment>
        )}
        <Segment>Username: </Segment>
        <Field type="text" name="username" />
        {touched.username && errors.username && (
          <Segment className="error">{errors.username}</Segment>
        )}
        <Segment>Password: </Segment>
        <Field type="password" name="password" />
        {touched.password && errors.password && (
          <Segment className="error">{errors.password}</Segment>
        )}
        <Segment>College/University:</Segment>
        <Field type="text" name="college" />
        {touched.college && errors.college && (
          <Segment className="error">{errors.college}</Segment>
        )}
        <Segment>Subject:</Segment>
        <Field type="text" name="subject" />
        {touched.subject && errors.subject && (
          <Segment className="error">{errors.subject}</Segment>
        )}
        <Segment>I want to use Better Professor To: </Segment>
        <Field component="select" name="reason">
          <option>Select a Reason</option>
          <option value="Better Communicate with my Students">
            Better Communicate with my Students
          </option>
          <option value="Be More Organized and Punctual">
            Be More Organized and Punctual
          </option>
          <option value="Consolidate My Planning into One Place">
            Consolidate My Planning into One Place
          </option>
          <option value="Other">Other</option>
        </Field>
        {touched.reason && errors.reason && (
          <Segment className="error">{errors.reason}</Segment>
        )}
        <Segment>If 'Other', Please Explain: </Segment>
        <Field type="text-area" name="other" />
        {touched.other && errors.other && (
          <Segment className="error">{errors.other}</Segment>
        )}
        <label>
          I Agree to BetterProfessor's Terms and Conditions
          <Field type="checkbox" name="terms" checked={values.terms} />
          {touched.terms && errors.terms && (
            <Segment className="error">{errors.terms}</Segment>
          )}
        </label>
        <button className="form-button">Sign Up!</button>
      </Form>
    </Container>
  );
}

const FormikSignUpForm = withFormik({
    mapPropsToValues(values) {
        return {
            firstName: values.firstName || '',
            lastName: values.lastName || '',
            email: values.email || '',
            username: values.username || '',
            password: values.password || '',
            college: values.college || '',
            subject: values.subject || '',
            reason: values.reason || '',
            other: values.other || '',
            terms: values.terms || false
        }
    },
    //=====VALIDATION SCHEMA=====
    validationSchema: Yup.object().shape({
        firstName: Yup.string()
        .required('First name can not be left blank'),
        lastName: Yup.string()
        .required('Last name can not be left blank'),
        email: Yup.string()
        .email('Please enter a valid email address')
        .required('Email can not be left blank'),
        username: Yup.string()
        .required('Please enter a valid username'),
        password: Yup.string()
        .min(7, 'Password must be at least 7 characters')
        .required('Please enter a valid password'), 
        college: Yup.string()
        .required('Please indicate at which college/university you teach'),
        subject: Yup.string()
        .required('Please enter the subject you teach'),
        reason: Yup.string()
        .required('Please select the reason for creating this account'),
        terms: Yup.bool()
        .oneOf([true], 'Please accept the Terms and Conditions')
        .required()
    }),
    //=====END OF VALIDATION SCHEMA=====
    handleSubmit(values, { setStatus, resetForm }) {
        axios.post('https://betterprofessor.herokuapp.com/api/register', values).then(res => {
            console.log('HTTP POST Response: ', res);
            setStatus(res.data);
            resetForm();
        })
    },
})(SignUp)

export default FormikSignUpForm
